// Initialize on document load
document.addEventListener('DOMContentLoaded', function () {
    // Set up theme toggle
    const themeToggles = document.querySelectorAll('.theme-toggle');
    themeToggles.forEach(toggle => {
        toggle.addEventListener('click', function (e) {
            e.preventDefault();
            const currentTheme = document.documentElement.getAttribute('data-theme') || 'light';
            const newTheme = currentTheme === 'light' ? 'dark' : 'light';
            setTheme(newTheme);
        });
    });

    // Check for saved theme preference or system preference
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme) {
        setTheme(savedTheme);
    } else if (window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches) {
        // Use dark theme if user prefers dark mode
        setTheme('dark');
    } else {
        // Default to light theme if no preference
        setTheme('light');
    }

    // Set up language switcher
    const languageItems = document.querySelectorAll('.language-item');
    languageItems.forEach(item => {
        item.addEventListener('click', function (e) {
            e.preventDefault();
            const lang = this.getAttribute('data-lang');
            if (lang) {
                setLanguage(lang);
            }
        });
    });

    // Check for saved language preference
    const savedLanguage = localStorage.getItem('selectedLanguage');
    if (savedLanguage) {
        setLanguage(savedLanguage);
    } else {
        // Default to English if no preference
        setLanguage('en');
    }

    // Set active nav link based on current page
    setActiveNavLink();
});

// Set theme function
function setTheme(theme) {
    theme = (theme === 'dark') ? 'dark' : 'light';
    document.documentElement.setAttribute('data-theme', theme);
    localStorage.setItem('theme', theme);

    const themeToggles = document.querySelectorAll('.theme-toggle');
    themeToggles.forEach(toggle => {
        const icon = toggle.querySelector('i');
        if (icon) {
            if (theme === 'dark') {
                icon.classList.remove('bi-moon');
                icon.classList.add('bi-sun');
            } else {
                icon.classList.remove('bi-sun');
                icon.classList.add('bi-moon');
            }
        }
    });
}

// Set language function
function setLanguage(lang) {
    if (!translations[lang]) {
        console.error('Invalid language:', lang);
        lang = 'en';
    }

    localStorage.setItem('selectedLanguage', lang);

    const languageDisplay = document.getElementById('languageDisplay');
    if (languageDisplay) {
        if (lang === 'en') {
            languageDisplay.innerHTML = '<i class="bi bi-globe"></i> EN';
        } else if (lang === 'ar') {
            languageDisplay.innerHTML = '<i class="bi bi-globe"></i> AR';
        } else if (lang === 'es') {
            languageDisplay.innerHTML = '<i class="bi bi-globe"></i> ES';
        }
    }

    if (lang === 'ar') {
        document.body.setAttribute('dir', 'rtl');
    } else {
        document.body.setAttribute('dir', 'ltr');
    }

    updateTranslations(lang);
}

// Update translations on the page
function updateTranslations(lang) {
    const t = translations[lang];
    if (!t) {
        console.error('Translation not found for language:', lang);
        return;
    }

    document.querySelectorAll('[data-i18n]').forEach(element => {
        const key = element.getAttribute('data-i18n');
        if (t[key]) {
            if (element.tagName === 'INPUT' || element.tagName === 'TEXTAREA') {
                element.placeholder = t[key];
            } else {
                element.textContent = t[key];
            }
        } else {
            console.warn(`Translation key not found: ${key} for language: ${lang}`);
        }
    });
}

// Set active nav link based on current page
function setActiveNavLink() {
    const currentPath = window.location.pathname;
    const navLinks = document.querySelectorAll('.navbar .nav-link');

    navLinks.forEach(link => {
        const href = link.getAttribute('href');
        if (!href) return;

        link.classList.remove('active');

        if (currentPath.endsWith(href) ||
            (href === 'index.html' && (currentPath.endsWith('/') || currentPath.endsWith('/index.html')))) {
            link.classList.add('active');
        }
    });
}

// Smooth scrolling for anchor links
document.addEventListener('click', function (e) {
    const target = e.target.closest('a');
    if (!target) return;

    const href = target.getAttribute('href');
    if (href && href.startsWith('#') && href.length > 1) {
        e.preventDefault();
        const element = document.querySelector(href);
        if (element) {
            element.scrollIntoView({
                behavior: 'smooth'
            });
        }
    }
});
